import './cart.css';
import Nav from '../Nav bar/Nav';
import Footer from '../footer section/footer';
import laptop from "../Home page/images/laptop.jpeg"
import iphone4 from "../Home page/images/iphone4.jpeg"
import iphone5 from "../Home page/images/iphone5.jpeg"
import Box from '../box product/box';
import { useState } from 'react';
const [cart ,setcart]= useState([])
const addToCart = (Box) =>{
  setcart ([...cart , Box])
}
function Cart() {
    const products =[

        {"id": "1", "brand": "Laptop Hp", "model": "Laptop", "price": "$499", "image_url": laptop},
        {"id": "2", "brand": "iphone14 pro", "model": "Iphone", "price": "$599", "image_url": iphone13},
        {"id": "3", "brand": "iphone 15 pro", "model": "Iphone", "price": "$699", "image_url": iphone133},
        {"id": "4", "brand": "iphone 13 pro max", "model": "Iphone", "price": "$799", "image_url": iphone4},
        {"id": "5", "brand": "Iphone 15 pro max", "model": "Iphone", "price": "$799", "image_url": iphone5},

    ]



  return (
    <div className="cart">
        <div className="cartsection">
            <div className="conut">
                <h1><span></span>Items  in your  cart</h1>
                <p>all the items aded into the basket or cart is listed out here! you can add more items or remove the one youre not conortable with</p>
            </div>

            <div className="items0">
                <h1>ITEMS</h1>
                <div className="last3">
                    <h1>QUANTITY</h1>
                    <h1>UNIT(PRICE)</h1>
                    <h1>Total</h1>
                </div>
                <div className="boxes">
                {products.map((item)=>(
                    <Box
                    image_url={item.image_url}
                    brand={item.brand}
                    model={item.model}
                    price={item.price}
                    onaddtocart={()=>{addToCart(item)}}
                    />
                ))}
                </div>

                <div className="cart">
                    <h4>cart</h4>
                    {cart.map((item , index))}
                </div>


            </div>

        </div>
{/* footer section */}
    <Footer/>
    </div>
  );
}

export default Cart;
