import "./sign.css";
import Nav from "../Nav bar/Nav";
import Footer from "../footer section/footer";
import img1 from "./images/im1.png";
import img2 from "./images/im2.png";
import { useState } from "react";
import valid from "./valid";
import {useFormik , Formik , Form , Field } from "formik"

const initialValues ={
    first: "",
    last: "",
    email: "",
    phone: "",
    email: "",
    password: "",
    cpassword: "",
}
function Sign() {
  let name = document.getElementById("name");

  return (
    <div className="sign">
      <div className="contt">

        <Formik
        initialValues={initialValues}
        validationSchema={<valid/>}
        >
             {({errors}) =>{
               <Form action="" >
               <h1>Sign Up</h1>
               <hr />
               <div className="inputss">
                 <Field type="text" name="first"   placeholder="First Name"></Field>
                 {/* <input
                   type="text"
                   id="name"
                   name="first"
                   maxLength="10"
                   placeholder="First Name"
                   required
                 /> */}

             <br />
             <Field type="text" name="last"   placeholder="Last Name"></Field>

                 {/* <input
                   type="text"
                   name="last"

                   maxLength="10"
                   placeholder="Last Name"
                   required
                 /> */}
                 <br />
                 <Field type="email" name="email"   placeholder="Email address"></Field>

                 {/* <input type="email" name="email" placeholder="Email address" required /> */}
                 <br />
                 <Field type="number" name="phone"   placeholder="Phone Number"></Field>

                 {/* <input
                   type="number"
                   maxLength="12"
                   name="phone"

                   placeholder="Phone Number"
                   required
                 /> */}
                 <input type="text" placeholder="Country" required />
                 <br />
                 <Field type="password" name="password"   placeholder="Password"></Field>
                 {errors.password && <small></small>}
     <br />
                 {/* <input type="password" name="password" placeholder="Password" required /> */}
                 <br />
                 <Field type="password" name="cpassword"   placeholder="Confirm password"></Field>

                 {/* <input type="password" name="cpassword" placeholder="Confirm Password" required /> */}
               </div>
               <button>SIGN UP</button>
               <h2>
                 I have an account already ! <a href="">Login</a>
               </h2>
             </Form>
        }
        }


        </Formik>
      </div>
      <div className="immmg2">
        <img src={img1} alt="" />
        <img src={img2} alt="" />
      </div>

      <Footer />
    </div>
  );
}

export default Sign;
