import React, { useState } from 'react';
import  Box  from '../box product/box.jsx'; 

const Hamd = () => {
  // Define your arrays
  const Daliydeal = [
    { brand: 'bedo', price: 1000 },
    { brand: 'Brand2', price: 2000 },
    // more items
  ];

  const Topdeals = [
    { brand: 'fashoin', price: 1500 },
    { brand: 'BrandB', price: 2500 },
    // more items
  ];

  // State to keep track of which array to display
  const [Daily, setCurrentArray] = useState(Daliydeal);

  // Function to switch between arrays
  const SwitchArray = () => {
    setCurrentArray(Daily === Daliydeal ? Topdeals : Daliydeal);
  };

  return (
    <div>
      {/* Button to switch arrays */}
      <button onClick={SwitchArray}>Fashion</button>

      {/* Render the current array */}
      <div>
        {Daily.map((e, index) => (
          <Box
            key={index}
            image_url={e.brand}
            price={e.price}
            brand={e.brand}
            disc="$1000" // This can be dynamic if needed
            model={e.brand}
          />
        ))}
      </div>
    </div>
  );
};

export default Hamd;
