import "./cart.css";
import Nav from "../Nav bar/Nav";
import Footer from "../footer section/footer";
import Box from "../box product/box";
import { useState } from "react";
import { Link } from "react-router-dom";
import { useEffect } from "react";
import Carditem from "../card item/carditem";

function Cart() {
  const [myCarts, setMyCarts] = useState(JSON.parse(localStorage.getItem("cart")) || [])
  // delete function
  const deleteCard = (id) => {
    // Filter out the item with the specified id
    const updatedCarts = myCarts.filter(i => i.id !== id);
    setMyCarts(updatedCarts);
  };

  useEffect(() => {
    // Update stoarge when data change
    localStorage.setItem("cart", JSON.stringify(myCarts));
  }, [myCarts]);

  //count buttons of product
  const [value , setvalue] = useState(1)


  const plus =(p)=>{
    setvalue(value + 1)
// total part
  }
  const mins =()=>{
    setvalue(value - 1)

    if (value === 0) {
      setvalue(0)

    }

  }
////////////


const Cart = ({ myCarts, deleteCard }) => {
  const [cartItems, setCartItems] = useState(
    myCarts.map(item => ({
      ...item,
      quantity: 1,
      totalPrice: item.price // Initialize with unit price
    }))
  );

  const updateCartItem = (id, quantityChange) => {
    setCartItems(prevItems =>
      prevItems.map(item =>
        item.id === id
          ? {
              ...item,
              quantity: Math.max(item.quantity + quantityChange, 0), // Prevent negative quantities
              totalPrice: (item.price + (quantityChange > 0 ? 2 : 0)) * (item.quantity + quantityChange)
            }
          : item
      )
    );
  };}



  return (
    <div className="cartt">

      <div className="cartsection">
        <div className="conut">
          <h1>
            <span></span>Items in your cart
          </h1>
          <p>
            all the items aded into the basket or cart is listed out here! you
            can add more items or remove the one youre not conortable with
          </p>
        </div>

        <div className="items0">
          <h1>ITEMS</h1>
          <div className="last3">
            <h1>QUANTITY</h1>
            <h1>UNIT(PRICE)</h1>
            <h1>Total</h1>
          </div>

          <div className="boxes">
          {myCarts.map((e)=>(
            <Carditem
            key={e.id}
            src={e.image}
            cat={e.category}
            title={e.title}
            price={e.price}
            deletCardd={()=>{deleteCard(e.id)}}
            pbtn={()=>{plus(e.price)}}
            mbtn={mins}
            QUANTITY={value}
            Total={() => {updateCartItem(e.id, 1)}}
            />
          ))}
          </div>


        </div>
        <div className="check">
          <h1>Total Amount :</h1>
          <span>$322</span>
          <p>Delivery fee is not included</p>
          <Link to="/home"> <button>Continue Shopping</button></Link>
          <button>Proceed to Checkout</button>

            </div>
            <Footer />

      </div>

    </div>

  );
}

export default Cart;
