import "./cart.css";
import Nav from "../Nav bar/Nav";
import Footer from "../footer section/footer";
import Box from "../box product/box";
import { useState } from "react";
import { Link } from "react-router-dom";
import Carditem from "../card item/carditem";

function Cart() {
  const [myCarts, setMyCarts] = useState(() => {
    // Initialize state with cart data from localStorage
    return JSON.parse(localStorage.getItem("cart")) || [];
  });

  useEffect(() => {
    // Update localStorage whenever myCarts changes
    localStorage.setItem("cart", JSON.stringify(myCarts));
  }, [myCarts]);

  const deleteCard = (id) => {
    // Filter out the item with the specified id
    const updatedCarts = myCarts.filter(item => item.id !== id);
    setMyCarts(updatedCarts);
  };

  return (
    <div className="cartt">

      <div className="cartsection">
        <div className="conut">
          <h1>
            <span></span>Items in your cart
          </h1>
          <p>
            all the items aded into the basket or cart is listed out here! you
            can add more items or remove the one youre not conortable with
          </p>
        </div>

        <div className="items0">
          <h1>ITEMS</h1>
          <div className="last3">
            <h1>QUANTITY</h1>
            <h1>UNIT(PRICE)</h1>
            <h1>Total</h1>
          </div>

          <div className="boxes">
          {myCarts.map((e)=>(
            <Carditem
            key={e.id}
            src={e.image}
            cat={e.category}
            title={e.title}
            price={e.price}
            deletCardd={()=>{deleteCard(e.id)}}
            />
          ))}
          </div>


        </div>
        <div className="check">
          <h1>Total Amount :</h1>
          <span>$322</span>
          <p>Delivery fee is not included</p>
          <Link to="/home"> <button>Continue Shopping</button></Link>
          <button>Proceed to Checkout</button>

            </div>
      </div>
      <Footer />
    </div>
  );
}

export default Cart;
