import './sign.css';
import Nav from '../Nav bar/Nav';
import Footer from '../footer section/footer';
import img1 from "./images/im1.png"
import img2 from "./images/im2.png"
import { useState } from 'react';
import valid from './valid';


function Sign() {
    let name = document.getElementById("name")

    const PasswordForm = () => {
        const [password, setPassword] = useState('');
        const [errors, setErrors] = useState([]);

        // Define validation rules
        const validatePassword = (password) => {
          const newErrors = [];

          if (password.length < 8) {
            newErrors.push('Password must be at least 8 characters long.');
          }
          if (!/[A-Z]/.test(password)) {
            newErrors.push('Password must contain at least one uppercase letter.');
          }
          if (!/[a-z]/.test(password)) {
            newErrors.push('Password must contain at least one lowercase letter.');
          }
          if (!/[0-9]/.test(password)) {
            newErrors.push('Password must contain at least one number.');
          }
          if (!/[!@#$%^&*]/.test(password)) {
            newErrors.push('Password must contain at least one special character.');
          }
        }}
          // Handle password change
  const handleChange = (e) => {
    const newPassword = e.target.value;
    setPassword(newPassword);
    setErrors(validatePassword(newPassword));
  };



  return (
    <div className="sign">
        <div className="contt">
            <form action="" onClick={handelvalid}>
                <h1>Sign Up</h1>
                <hr />
                <div className="inputss">
                    <input type="text" id='name'  maxLength='10' placeholder='First Name' required onChange={handlclick}/>
                    {errors.email && <p style={{color:"red"}}>{errors.name}</p>}
                    <input type="text" maxLength='10'  placeholder='Last Name' required/>
                    <input type="email"  placeholder='Email address' required/>
                    <input type="number" maxLength="12"  placeholder='Phone Number' required/>
                    <input type="text"  placeholder='Country' required/>
                    <input type="password"  pattern='^(?=.*[a-zA-Z])(?=.*\d)(?=.*[!@#$%^&*()_+])[A-Za-z\d!@#$%^&*()_+]{8,20}'
                     placeholder='Password' required/>
                    <input type="password"  placeholder='Confirm Password' required/>
                </div>
                <button>SIGN UP</button>
                <h2>I have an account already ! <a href="">Login</a></h2>
            </form>
        </div>
        <div className="immmg2">
            <img src={img1} alt="" />
            <img src={img2} alt="" />
        </div>


    <Footer/>
    </div>
  );
}

export default Sign;
