import './sign.css';
import Nav from '../Nav bar/Nav';
import Footer from '../footer section/footer';
import img1 from "./images/im1.png"
import img2 from "./images/im2.png"

function Sign() {
    let firstinput = document.getElementById("firstinput")
     function myfun() {
          if (firstinput=== "") {
        alert("please fill firstname")
    }else 
    alert()

     }





  return (
    <div className="sign">
        <div className="contt">
            <form action="">
                <h1>Sign Up</h1>
                <hr />
                <div className="inputss">
                    <input type="text" id='firstinput'  maxLength='10' placeholder='First Name' required/>
                    <input type="text" maxLength='10'  placeholder='Last Name' required/>
                    <input type="email"  placeholder='Email address' required/>
                    <input type="number" maxLength="12"  placeholder='Phone Number' required/>
                    <input type="text"  placeholder='Country' required/>
                    <input type="password"  pattern='^(?=.*[a-zA-Z])(?=.*\d)(?=.*[!@#$%^&*()_+])[A-Za-z\d!@#$%^&*()_+]{8,20}'
                     placeholder='Password' required/>
                    <input type="password"  placeholder='Confirm Password' required/>
                </div>
                <button onClick={myfun}>SIGN UP</button>
                <h2>I have an account already ! <a href="">Login</a></h2>
            </form>
        </div>
        <div className="immmg2">
            <img src={img1} alt="" />
            <img src={img2} alt="" />
        </div>


    <Footer/>
    </div>
  );
}

export default Sign;
