    <div className="checkk">
  <div className="checkksection">
    <header>
      <Link to="/market"><button>-Continue Shopping </button></Link>
      <hr />

      <div className="boxe">
      {myCarts.map(e => (
              <Carditem
                key={e.id}
                src={e.image}
                cat={e.category}
                title={e.title}
                price={e.Total}
                deletCardd={() => deleteCard(e.id)}
                pbtn={() => plusbtn(e.price)}
                mbtn={() => minsbtn(e.price)}
                QUANTITY={value}
              />
            ))}
      </div>

    </header>

  </div>

        <Footer/>
    </div>
  );
}

export default Check;





