    setvalue(value - 1)

    if (value === 0) {
      setvalue(0)

    }

  }



  return (
    <div className="cartt">
                <div className="boxes">
          {myCarts.map((e)=>(
            <Carditem
            key={e.id}
            src={e.image}
            cat={e.category}
            title={e.title}
            price={e.price}
            deletCardd={()=>{deleteCard(e.id)}}
