import './App.css';
import {BrowserRouter ,Routes,Route } from "react-router-dom"

import Home from './Home page/home';
import Market from './market store/market';
import Contact from './contact page/contact';
import Shop from './shop/shop'; 
import About from './about us/about';
import Sign from './sign up/sign';
import Login from './slogin/login';
import Product from './Product destails/product';
import Cart from './cart/cart';
import Errorr from './error/error';
import Nav from './Nav bar/Nav';
import Footer from './footer section/footer';
import Check from './checkout/check';
function App() {

  //  use state to do addtocart count and else
  const [data, setData] = useState([]);
  const [cartCount, setCartCount] = useState();
  // use effect to get the api when web is load
  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((res) => res.json())
      .then((data) => setData(data))
      .catch((error) => console.log(error));
  }, []);
  // function of add to cart
  const addToCart = (productItem) => {
    // let my cart the empty array let the inner of it be from string to object
    const myCart = JSON.parse(localStorage.getItem("cart")) || [];
    //push the props to array
    myCart.push(productItem);
    // set cart into storage after let it string
    localStorage.setItem("cart", JSON.stringify(myCart));
    // to make  cart count
    setCartCount(myCart.length);
    localStorage.setItem("length", cartCount);}
  return (
    <div className="App">
     <BrowserRouter>
     <Nav/>
     <Routes>
      <Route path='/' element={<Home/>}/>
      <Route path='*' element={<Errorr/>}/>
      <Route path='/home' element={<Home/>}/>
      <Route path='/shop' element={<Shop/>}/>
      <Route path='/about' element={<About/>}/>
      <Route path='/contact' element={<Contact/>}/>
      <Route path='/login' element={<Login/>}/>
      <Route path='/sign' element={<Sign/>}/>
      <Route path='/Market' element={<Market/>}/>
      <Route path='/product' element={<Product/>}/>
      <Route path='/cart' element={<Cart/>}/>
      <Route path='/check' element={<Check/>}/>
      <Route path='/product' element={<Product/>}/>
     </Routes>
     </BrowserRouter>



    </div>
  );
}

export default App;
